        /// <summary>
        /// Configures the HttpClients.
        /// </summary>
        /// <param name="client"></param>
        /// <param name="builder"></param>
        /// <returns></returns>
        public HostConfiguration AddApiHttpClients
        (
            Action<HttpClient>? client = null, Action<IHttpClientBuilder>? builder = null)
        {
            if (client == null)
                client = c => c.BaseAddress = new Uri(ClientUtils.BASE_ADDRESS);

            List<IHttpClientBuilder> builders = new List<IHttpClientBuilder>();

            builders.Add(_services.AddHttpClient<IDefaultApi, DefaultApi>(client));
            builders.Add(_services.AddHttpClient<IClipzyApi, ClipzyApi>(client));
            builders.Add(_services.AddHttpClient<IConvertApi, ConvertApi>(client));
            builders.Add(_services.AddHttpClient<IDailyApi, DailyApi>(client));
            builders.Add(_services.AddHttpClient<IGameApi, GameApi>(client));
            builders.Add(_services.AddHttpClient<IImageApi, ImageApi>(client));
            builders.Add(_services.AddHttpClient<IMiscApi, MiscApi>(client));
            builders.Add(_services.AddHttpClient<INetworkApi, NetworkApi>(client));
            builders.Add(_services.AddHttpClient<IPoemApi, PoemApi>(client));
            builders.Add(_services.AddHttpClient<IRandomApi, RandomApi>(client));
            builders.Add(_services.AddHttpClient<ISocialApi, SocialApi>(client));
            builders.Add(_services.AddHttpClient<IStatusApi, StatusApi>(client));
            builders.Add(_services.AddHttpClient<ITextApi, TextApi>(client));
            builders.Add(_services.AddHttpClient<ITranslateApi, TranslateApi>(client));
            builders.Add(_services.AddHttpClient<IWebParseApi, WebParseApi>(client));
            
            if (builder != null)
                foreach (IHttpClientBuilder instance in builders)
                    builder(instance);

            HttpClientsAdded = true;

            return this;
        }

